// Output Check - Original version
const aiResponse = $json.output?.toString().trim() || 'No response generated';

return {
  json: {
    session_id: $json.session_id || `sess_${Math.random().toString(36).substring(2, 10)}`,
    customer_message: $json.customer_message || 'No message',
    chatbot_response: aiResponse,
    timestamp: new Date().toISOString(),

    // Add these for the database nodes
    agent_response: aiResponse,
    site_key: $json.site_key || null,
    domain: $json.domain || null,
    user_message: $json.customer_message || 'No message',
    license_key: $json.license_used || null
  }
};

// Enhanced Input Check for Setup Agent workflow

const incoming = $json.body || $json;

// Extract message and session
const user_message = incoming.user_message || incoming.message || "No message";
const session_id = incoming.session_id || `sess_${Math.random().toString(36).substring(2, 10)}`;

// Extract domain if provided (might be in the message or separate field)
let domain = incoming.domain || incoming.shop_domain || null;

// If domain is not in a field, try to extract from message
if (!domain && user_message) {
  // More comprehensive regex to find domain-like strings
  const domainMatch = user_message.match(/(?:(?:my\s+)?(?:domain|site|website)\s+is\s+)?([a-zA-Z0-9][a-zA-Z0-9-]{0,61}[a-zA-Z0-9]\.(?:[a-zA-Z]{2,}|(?:com|net|org|io|uk|co|shop|store)))/i);
  if (domainMatch) {
    domain = domainMatch[1].toLowerCase();
  }
}

// Extract site_key if provided
const site_key = incoming.site_key || null;

// Add tool usage hints for the agent
let tool_hint = "";
if (domain && !site_key) {
  tool_hint = "IMPORTANT: A domain has been provided. You must use the 'Request Site Key' tool to generate a site key after confirmation.";
}

// Check if user is confirming (saying yes)
const is_confirming = user_message.toLowerCase().match(/^(yes|y|yep|yeah|correct|confirm|sure|ok|that's right|that is right)$/i);

return {
  json: {
    user_message: user_message,
    session_id: session_id,
    domain: domain,
    site_key: site_key,
    is_confirming: !!is_confirming,
    tool_hint: tool_hint,
    // Add a system instruction that gets passed to the agent
    system_context: domain && is_confirming 
      ? "USER HAS CONFIRMED DOMAIN. YOU MUST NOW USE THE 'Request Site Key' TOOL TO GENERATE A REAL SITE KEY."
      : ""
  }
};

// Site Key Generator - Fixed version without memory reference
function generateKey(length = 16) {
  const chars = 'abcdefghijklmnopqrstuvwxyz0123456789';
  let result = '';
  for (let i = 0; i < length; i++) {
    result += chars.charAt(Math.floor(Math.random() * chars.length));
  }
  return result;
}

// Get agent output
const agent_output = $json;
const agent_message = agent_output.output || agent_output.text || agent_output.message || '';

// Get input data for session info
const inputData = $('Input Check').first().json;
const session_id = inputData.session_id;
const user_message = inputData.user_message || '';

// Check if user provided a license
const licensePattern = /\b(INTL-[A-Z0-9]{4}-[A-Z0-9]{4}-[A-Z0-9]{4})\b/i;
const userLicense = user_message.match(licensePattern);

// Check if the agent is showing the process steps
const isShowingProcess = agent_message.includes('âœ“ License validated') || 
                        agent_message.includes('Let me set up your chatbot');

// Get domain from the agent's message or input data
let domain = inputData.domain || null;

// Check the agent's current message for domain confirmation
const currentDomainMatch = agent_message.match(/confirm that ([a-zA-Z0-9][a-zA-Z0-9-]{0,61}[a-zA-Z0-9]\.(?:com|net|org|io|uk|co|shop|store)) is your domain/i);
if (currentDomainMatch) {
  domain = currentDomainMatch[1];
}

// Also check for domain in various message patterns
if (!domain) {
  // Pattern 1: "Your site key for domain.com is..."
  const domainPattern1 = agent_message.match(/for ([a-zA-Z0-9][a-zA-Z0-9-]{0,61}[a-zA-Z0-9]\.(?:com|net|org|io|uk|co|shop|store))/i);
  if (domainPattern1) {
    domain = domainPattern1[1];
  }
}

// Check if domain was in the user's original message
if (!domain && inputData.user_message) {
  const userDomainMatch = inputData.user_message.match(/([a-zA-Z0-9][a-zA-Z0-9-]{0,61}[a-zA-Z0-9]\.(?:com|net|org|io|uk|co|shop|store))/i);
  if (userDomainMatch) {
    domain = userDomainMatch[1];
  }
}

let final_message = agent_message;
let site_key = null;
let license_used = null;
let generate_key = false;

// If agent is showing process steps (license was validated)
if (isShowingProcess) {
  // Generate the key
  site_key = `key_${generateKey(16)}`;
  generate_key = true;

  // Get license from user message
  if (userLicense) {
    license_used = userLicense[1].toUpperCase();
  }

  // Replace any placeholder keys in the message
  final_message = agent_message.replace(/key_[a-zA-Z0-9]+/g, site_key);
}

// Pass everything forward with ALL required fields
return {
  json: {
    ...agent_output,
    output: final_message,
    agent_message: final_message,
    session_id: session_id,
    site_key: site_key,
    domain: domain || 'unknown',
    license_provided: !!userLicense,
    license_used: license_used,
    license_key: license_used, // Add this for Site Key Store
    generate_key: generate_key  // Add this flag for Site Key Store
  }
};
